sources.styles/utils/_mixins.scss:
@mixin hover($_property, $_toValue) {
  transition: #{$_property} 0.4s ease-in-out;
  &:hover {
    cursor: pointer;
    #{$_property}: $_toValue;
  }
}

@mixin onTablet {
  @media (min-width: 768px) {
    @content;
  }
}

@mixin onDesktop {
  @media (min-width: 1200px) {
    @content;
  }
}

@mixin onLargeScreen {
  @media (min-width: 1440px) {
    @content;
  }
}

@mixin grid {
  --columns: 2;
  --gap: 16px;

  display: grid;
  grid-template-columns: repeat(var(--columns), 1fr);
  column-gap: var(--gap);

  @include onTablet {
    --columns: 8;
    --gap: 24px;
  }
}

@mixin text {
  font-weight: 400;
  font-size: 16px;
  line-height: 24px;
  text-align: center;
}

@mixin bigText {
  font-weight: 400;
  font-size: 96px;
  line-height: 82px;
  letter-spacing: -0.05em;
}

@mixin mobileHeading {
